def pattern_matching(picture,part):
    # Get the dimensions of picture and part
    num = []
    for element in part:
        num.append(list(element))
    rows_picture, cols_picture = len(picture), len(picture[0])
    rows_part, cols_part = len(num), len(num[0])

    # List to store results
    matches = []

    # Loop through picture
    for i in range(rows_picture - rows_part + 1): # +1 ensures we don't go out of bounds
        for j in range(cols_picture - cols_part + 1):
            sub_matrix = [ row[j:j+cols_part] for row in picture[i:i+rows_part] ]
            print(sub_matrix)
            if sub_matrix == num:
                matches.append((i, j, i+rows_part-1, j+cols_part-1))

    return matches

